import React, { useState } from "react";
import BookingForm from "./components/BookingForm";
import SwipableCards from "./components/SwipableCards";
import "./App.css";

import FacebookIcon from "@mui/icons-material/Facebook";
import InstagramIcon from "@mui/icons-material/Instagram";
import TwitterIcon from "@mui/icons-material/Twitter";
import WhatsAppIcon from "@mui/icons-material/WhatsApp";

import image2 from "./assets/image2.png";

function App() {
  const [bookingSuccess, setBookingSuccess] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  const handleBooking = async (form) => {
    try {
      const response = await fetch("http://localhost:8080/api/bookings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      if (response.ok) {
        setBookingSuccess(true);
      } else {
        setBookingSuccess(false);
      }
    } catch (error) {
      console.error(error);
      setBookingSuccess(false);
    }
  };

  return (
    <div className="app-container">
      {/* ✅ Navbar */}
      <div className="top">
        <div className="logo">Sarathi</div>
        <nav className="navbar">
          <div
            className={`menu-toggle ${menuOpen ? "open" : ""}`}
            onClick={() => setMenuOpen(!menuOpen)}
          >
            ☰
          </div>
          <ul className={menuOpen ? "nav-links active" : "nav-links"}>
            <li>
              <a
                href="#home"
                onClick={(e) => {
                  e.preventDefault();
                  document
                    .getElementById("home")
                    .scrollIntoView({ behavior: "smooth" });
                  setMenuOpen(false);
                }}
              >
                Home
              </a>
            </li>
            <li>
              <a
                href="#about"
                onClick={(e) => {
                  e.preventDefault();
                  document
                    .getElementById("about")
                    .scrollIntoView({ behavior: "smooth" });
                  setMenuOpen(false);
                }}
              >
                About Us
              </a>
            </li>
            <li>
              <a
                href="#services"
                onClick={(e) => {
                  e.preventDefault();
                  document
                    .getElementById("services")
                    .scrollIntoView({ behavior: "smooth" });
                  setMenuOpen(false);
                }}
              >
                Services
              </a>
            </li>
            <li>
              <a
                href="#contact"
                onClick={(e) => {
                  e.preventDefault();
                  document
                    .getElementById("contact")
                    .scrollIntoView({ behavior: "smooth" });
                  setMenuOpen(false);
                }}
              >
                Contact
              </a>
            </li>
          </ul>
        </nav>
      </div>

      {/* ✅ Hero Section */}
      <div id="home" className="hero">
        <h1>Welcome To Sarathi.</h1>
        <p>Book a professional driver for your vehicle anytime, anywhere.</p>
        <h2>Book your Ride By Click Here!</h2>
        <button
          className="glow-btn"
          onClick={() =>
            document
              .getElementById("booking")
              .scrollIntoView({ behavior: "smooth" })
          }
        >
          Book Now
        </button>
      </div>

      {/* ✅ About Section */}
      <div id="about" className="section">
        <div className="about-section">
          <h2>About Us</h2>
          <div className="about-content">
            <div>
              <ul>
                <li>We connect customers with professional drivers.</li>
                <li>
                  Whether you need a driver for a few hours or a full day, we
                  make it simple and safe.
                </li>
              </ul>
            </div>
            <div className="about-image">
              <img src={image2} alt="About Sarathi" />
            </div>
          </div>
        </div>
      </div>

      {/* ✅ Services Section */}
      <div id="services" className="section">
        <SwipableCards />
      </div>

      {/* ✅ Booking Form Section */}
      <div id="booking" className="booking-section">
        <BookingForm onSubmit={handleBooking} />
        {bookingSuccess && (
          <p className="success-msg">✅ Booking confirmed successfully!</p>
        )}
      </div>

      {/* ✅ Contact Section */}
      <div id="contact" className="section">
        <h2>Contact Us</h2>
        <p>Email: support@driverondemand.com</p>
        <p>Phone: +91-9876543210</p>
      </div>

      {/* ✅ Footer */}
      <div className="footer-section">
        <div>
          <div className="heading">
            <p>BUSINESS TIME</p>
          </div>
          <p className="list">24/7 service</p>
        </div>

        <div>
          <div className="heading">
            <p>QUICK ACCESS</p>
          </div>
          <div className="list">
            <ul className="foot-links">
              <li>
                <a
                  href="#home"
                  onClick={(e) => {
                    e.preventDefault();
                    document
                      .getElementById("home")
                      .scrollIntoView({ behavior: "smooth" });
                    setMenuOpen(false);
                  }}
                >
                  Home
                </a>
              </li>
              <li>
                <a
                  href="#services"
                  onClick={(e) => {
                    e.preventDefault();
                    document
                      .getElementById("services")
                      .scrollIntoView({ behavior: "smooth" });
                    setMenuOpen(false);
                  }}
                >
                  Services
                </a>
              </li>
              <li>
                <a
                  href="#about"
                  onClick={(e) => {
                    e.preventDefault();
                    document
                      .getElementById("about")
                      .scrollIntoView({ behavior: "smooth" });
                    setMenuOpen(false);
                  }}
                >
                  About
                </a>
              </li>
              <li>
                <a
                  href="#booking"
                  onClick={(e) => {
