import React from 'react';

export default function DriverList({drivers}){
  return (
    <div>
      <h3>Available Drivers</h3>
      {(!drivers || drivers.length===0) && <p className="small">No drivers available</p>}
      <div style={{marginTop:8}}>
        {(drivers || []).map(d=> (
          <div key={d.id} className="driver-item">
            <div className="driver-avatar">{(d.name || 'D').slice(0,2).toUpperCase()}</div>
            <div className="driver-meta">
              <div className="name">{d.name}</div>
              <div className="meta">Experience: {d.experience} yrs · License: {d.licenseType}</div>
            </div>
            <div style={{textAlign:'right', minWidth:90}}>
              <div style={{fontWeight:700}}>₹{d.hourlyRate}</div>
              <div className="small">per hour</div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
