import React, { useState, useEffect } from 'react';

export default function BookingForm({onSubmit, drivers}){
  const [form, setForm] = useState({
    customerName:'',
    phone:'',
    pickupLocation:'',
    pickupTime:'',
    durationHours:1,
    driverId: ''
  });

  useEffect(()=>{
    if(drivers && drivers.length && !form.driverId){
      setForm(prev => ({...prev, driverId: drivers[0].id}));
    }
  }, [drivers]); // set default driver when drivers load

  function update(e){
    const {name, value} = e.target;
    setForm(prev => ({...prev, [name]: value}));
  }

  function submit(e){
    e.preventDefault();
    onSubmit(form);
  }

  return (
    <form onSubmit={submit} className="card" style={{padding:0}}>
      <div style={{padding:16}}>
        <h3>Book a Driver</h3>

        <div className="form-row">
          <label>Customer Name</label>
          <input className="input" name="customerName" value={form.customerName} onChange={update} required />
        </div>

        <div className="form-row">
          <label>Phone</label>
          <input className="input" name="phone" value={form.phone} onChange={update} required />
        </div>

        <div className="form-row">
          <label>Pickup Location</label>
          <input className="input" name="pickupLocation" value={form.pickupLocation} onChange={update} required />
        </div>

        <div className="form-row">
          <label>Pickup Time</label>
          <input className="input" type="datetime-local" name="pickupTime" value={form.pickupTime} onChange={update} required />
        </div>

        <div className="form-row">
          <label>Duration (hours)</label>
          <input className="input" type="number" name="durationHours" min={1} value={form.durationHours} onChange={update} />
        </div>

        <div className="form-row">
          <label>Choose Driver</label>
          <select className="input" name="driverId" value={form.driverId || ''} onChange={update}>
            {(drivers || []).map(d=> <option key={d.id} value={d.id}>{d.name} — {d.experience} yrs</option>)}
          </select>
        </div>

        <div style={{marginTop:12, display:'flex', justifyContent:'space-between', alignItems:'center'}}>
          <div className="small form-note">Driver arrival times are confirmed after booking.</div>
          <button className="btn btn-primary" type="submit">Confirm Booking</button>
        </div>
      </div>
    </form>
  );
}
