import React, { useState } from "react";
import axios from "axios";
import "./BookingForm.css";
import LocationSelector from "./LocationSelector";

const ORS_API_KEY = "YOUR_ORS_API_KEY"; // <-- Replace with your key

export default function BookingForm({ onSubmit }) {
  const [form, setForm] = useState({
    customerName: "",
    phone: "",
    pickupTime: "",
    service: "hourly",
    durationHours: 1,
    pickupLat: null,
    pickupLng: null,
    pickupAddress: "",
    dropLat: null,
    dropLng: null,
    dropAddress: "",
  });

  const [submitting, setSubmitting] = useState(false);
  const [distanceKm, setDistanceKm] = useState(null);
  const [estimatedFare, setEstimatedFare] = useState(null);

  const update = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const calculateDistanceAndFare = async (pickup, drop) => {
    if (!pickup || !drop) return;
    try {
      const response = await axios.post(
        "https://api.openrouteservice.org/v2/directions/driving-car",
        {
          coordinates: [
            [pickup.lng, pickup.lat],
            [drop.lng, drop.lat],
          ],
        },
        {
          headers: {
            Authorization: ORS_API_KEY,
            "Content-Type": "application/json",
          },
        }
      );

      const distanceMeters = response.data?.routes?.[0]?.summary?.distance;
      if (!distanceMeters) {
        console.error("No distance returned by ORS API", response.data);
        setDistanceKm(null);
        setEstimatedFare(null);
        return;
      }

      const km = (distanceMeters / 1000).toFixed(2);
      setDistanceKm(km);
      setEstimatedFare((km * 15).toFixed(0)); // â‚¹15/km
    } catch (err) {
      console.error("Error fetching route:", err);
      setDistanceKm(null);
      setEstimatedFare(null);
    }
  };

  const handlePickup = (pickup) => {
    setForm((prev) => ({
      ...prev,
      pickupLat: pickup.lat,
      pickupLng: pickup.lng,
      pickupAddress: pickup.address,
    }));
    if (form.dropLat && form.dropLng) calculateDistanceAndFare(pickup, form);
  };

  const handleDrop = (drop) => {
    setForm((prev) => ({
      ...prev,
      dropLat: drop.lat,
      dropLng: drop.lng,
      dropAddress: drop.address,
    }));
    if (form.pickupLat && form.pickupLng) calculateDistanceAndFare(form, drop);
  };

  const submit = async (e) => {
    e.preventDefault();
    if (!form.pickupLat || !form.dropLat) {
      alert("Please select both pickup and drop locations!");
      return;
    }

    setSubmitting(true);
    try {
      await onSubmit(form);
      setForm({
        customerName: "",
        phone: "",
        pickupTime: "",
        service: "hourly",
        durationHours: 1,
        pickupLat: null,
        pickupLng: null,
        pickupAddress: "",
        dropLat: null,
        dropLng: null,
        dropAddress: "",
      });
      setDistanceKm(null);
      setEstimatedFare(null);
    } catch (error) {
      console.error(error);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <form onSubmit={submit} className="booking-form card">
      <h3>Book a Driver</h3>

      <div className="form-row">
        <input
          className="input"
          name="customerName"
          value={form.customerName}
          onChange={update}
          placeholder=" "
          required
        />
        <label>Customer Name</label>
      </div>

      <div className="form-row">
        <input
          className="input"
          name="phone"
          value={form.phone}
          onChange={update}
          placeholder=" "
          required
        />
        <label>Phone</label>
      </div>

      <div className="form-row">
        <input
          className="input"
          type="datetime-local"
          name="pickupTime"
          value={form.pickupTime}
          onChange={update}
          placeholder=" "
          required
        />
        <label>Pickup Time</label>
      </div>

      <div className="form-row">
        <select
          className="input"
          name="service"
          value={form.service}
          onChange={update}
        >
          <option value="hourly">Hourly Driver</option>
          <option value="fullDay">Full Day</option>
          <option value="outstation">Outstation</option>
          <option value="night">Night Shift</option>
          <option value="event">Event</option>
          <option value="airport">Airport Pickup</option>
        </select>
        <label>Service Type</label>
      </div>

      {form.service === "hourly" && (
        <div className="form-row">
          <input
            className="input"
            type="number"
            name="durationHours"
            min={1}
            value={form.durationHours}
            onChange={update}
          />
          <label>Duration (hours)</label>
        </div>
      )}

      <LocationSelector type="pickup" setLocation={handlePickup} />
      <LocationSelector type="drop" setLocation={handleDrop} />

      {distanceKm && (
        <div className="fare-display">
          <p>Estimated Distance: <strong>{distanceKm} km</strong></p>
          <p>Estimated Fare: <strong>â‚¹{estimatedFare}</strong></p>
        </div>
      )}

      <div className="form-action">
        <button type="submit" className="btn" disabled={submitting}>
          {submitting ? "Booking..." : "Confirm Booking"}
        </button>
      </div>
    </form>
  );
}
