import React, { useEffect, useState } from 'react';
import BookingForm from './components/BookingForm';
import DriverList from './components/DriverList';
import { fetchDrivers, createBooking } from './api';

export default function App(){
  const [drivers, setDrivers] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(()=>{
    fetchDrivers().then(setDrivers).catch(err=>console.error(err));
  },[]);

  async function handleBooking(data){
    try{
      const res = await createBooking(data);
      setMessage('Booking successful! Booking id: ' + res.id);
    }catch(e){
      console.error(e);
      setMessage('Booking failed');
    }
  }

  return (
    <div style={{maxWidth:900, margin:'20px auto', fontFamily:'Arial, sans-serif'}}>
      <header>
        <h1>Hire a Driver for Your Vehicle</h1>
        <p>Use your vehicle â€” book a professional driver.</p>
      </header>

      <main style={{display:'grid', gridTemplateColumns:'1fr 1fr', gap:20}}>
        <div>
          <BookingForm onSubmit={handleBooking} drivers={drivers} />
          {message && <p>{message}</p>}
        </div>
        <div>
          <DriverList drivers={drivers} />
        </div>
      </main>
    </div>
  );
}
