import React, { useEffect, useState } from 'react';
import BookingForm from './components/BookingForm';
import DriverList from './components/DriverList';
import { fetchDrivers, createBooking } from './api';
import './index.css';

export default function App(){
  const [drivers, setDrivers] = useState([]);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState(''); // 'success' or 'error'

  useEffect(()=>{
    fetchDrivers().then(setDrivers).catch(err=>{
      console.error(err);
      setMessage('Could not load drivers');
      setMessageType('error');
    });
  },[]);

  async function handleBooking(data){
    try{
      const res = await createBooking(data);
      setMessage('Booking successful! Booking id: ' + res.id);
      setMessageType('success');
    }catch(e){
      console.error(e);
      setMessage('Booking failed');
      setMessageType('error');
    }
  }

  return (
    <div className="app-container">
      <header className="header">
        <div className="header-left">
          <h1>Hire a Driver for Your Vehicle</h1>
          <p>Use your own vehicle — book a professional driver on-demand.</p>
        </div>
        <div className="header-right small">Safe · Verified · Professional</div>
      </header>

      <main className="main-grid">
        <div className="card form-container">
          <BookingForm onSubmit={handleBooking} drivers={drivers} />
          {message && (
            <div style={{marginTop:12}}>
              <div className={messageType === 'success' ? 'success-msg' : 'error-msg'}>
                {message}
              </div>
            </div>
          )}
        </div>

        <aside className="right-section">
          <div className="card driver-list">
            <DriverList drivers={drivers} />
          </div>

          <div className="card">
            <h4 style={{marginTop:0}}>How it works</h4>
            <ol style={{marginTop:8, color:'#374151'}}>
              <li>Fill booking details & pick a driver.</li>
              <li>Driver arrives and drives your vehicle as agreed.</li>
              <li>Payment & rating handled after service (future feature).</li>
            </ol>
            <p className="form-note">This is a starter MVP. Add authentication, availability, and payments next.</p>
          </div>
        </aside>
      </main>

      <div className="footer-note">© {new Date().getFullYear()} Driver-on-Demand — Made with care</div>
    </div>
  );
}
