import React, { useState, useEffect } from "react";
import "./ServicesCarousel.css";

const ServicesCarousel = () => {
  const services = [
    { id: 1, title: "Hourly Driver", img: "/assets/driver.png" },
    { id: 2, title: "Full Day Driver", img: "/assets/driver1.png" },
    { id: 3, title: "Outstation Travel", img: "/assets/outstation.jpg" },
    { id: 4, title: "Event Driver", img: "/assets/event.jpg" },
    { id: 5, title: "Night Shift", img: "/assets/night.jpg" },
    { id: 5, title: "Airport Pickup", img: "/assets/airport.jpg" }
  ];

  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent((prev) => (prev + 1) % services.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [services.length]);

  return (
    <div className="carousel3d-section">
      <h2 className="carousel-title">Our Services</h2>
      <div className="carousel3d-wrapper">
        <div
          className="carousel3d"
          style={{ transform: `rotateY(${-current * (360 / services.length)}deg)` }}
        >
          {services.map((service, index) => (
            <div
              key={service.id}
              className="carousel3d-card"
              style={{
                transform: `rotateY(${index * (360 / services.length)}deg) translateZ(350px)`
              }}
            >
              <img src={service.img} alt={service.title} />
              <h3>{service.title}</h3>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ServicesCarousel;
