import React, { useEffect, useState } from "react";
import BookingForm from "./components/BookingForm";
import "./App.css";
import FacebookIcon from '@mui/icons-material/Facebook';
import InstagramIcon from '@mui/icons-material/Instagram';
import TwitterIcon from '@mui/icons-material/Twitter';
import WhatsAppIcon from '@mui/icons-material/WhatsApp';
import image2 from "./assets/image2.png";
import ServicesCarousel from "./components/ServicesCarosel";
import ScrollReveal from "scrollreveal";
import Typed from "typed.js";
import anime from "animejs";

function App() {
  const [bookingSuccess, setBookingSuccess] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  const [bookingError, setBookingError] = useState("");


  useEffect(() => {
    const typed = new Typed('.multiple-text', {
      strings: [
        "Sarathi — Your Personal Driver Partner",
        "Book Reliable Drivers Anytime, Anywhere",
        "Safe, Comfortable, and Hassle-Free Rides",
        "Experience the Smart Way to Travel"
      ],
      typeSpeed: 60,
      backSpeed: 40,
      backDelay: 1000,
      loop: true,
      showCursor: true,
      cursorChar: "|",
    });


    ScrollReveal().reveal('.section', { duration: 2000, distance: '80px', easing: 'ease-in-out', origin: 'bottom', delay:200, reset: false });
    ScrollReveal().reveal('.home-content' ,{ duration: 2000, distance: '80px', easing: 'ease-in-out', origin: 'bottom', delay:200, reset: false });

     return () => {
      typed.destroy();
    };

  },[]);
 useEffect(() => {
    const text = document.querySelector(".ml3");
    text.innerHTML = text.textContent.replace(/\S/g, "<span class='letter'>$&</span>");

    anime.timeline({ loop: true })
      .add({
        targets: ".ml3 .letter",
        opacity: [0, 1],
        easing: "easeInOutQuad",
        duration: 1000,
        delay: (el, i) => 150 * (i + 1)
      }).add({
        targets: ".ml3",
        opacity: 0,
        duration: 1000,
        easing: "easeOutExpo",
        delay: 1000
      });
  }, []);
const handleBooking = async (form) => {
  setBookingError(""); // reset previous errors
  try {
    const response = await fetch("http://localhost:8080/api/bookings", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });

    const data = await response.json();

    if (!response.ok) {
      setBookingError(data?.message || "Booking failed, please check your input!");
      return;
    }

    setBookingSuccess(true);
    setBookingError(""); // clear any previous error

    setTimeout(() => setBookingSuccess(false), 3000);
  } catch (error) {
    console.error("Booking error:", error);
    setBookingError("Network error! Please try again later.");
  }
};



  return (
    <div className="app-container">
      {/* Navbar */}
      <header className="top">
        <div className="logo">Sarathi</div>
        <nav className="navbar">
          <div
            className={`menu-toggle ${menuOpen ? "open" : ""}`}
            onClick={() => setMenuOpen(!menuOpen)}
          >
            ☰
          </div>
          <ul className={menuOpen ? "nav-links active" : "nav-links"}>
            {["home", "about", "services", "booking", "contact"].map((id) => (
              <li key={id}>
                <a
                  href={`#${id}`}
                  onClick={(e) => {
                    e.preventDefault();
                    document.getElementById(id).scrollIntoView({ behavior: "smooth" });
                    setMenuOpen(false);
                  }}
                >
                  {id.charAt(0).toUpperCase() + id.slice(1)}
                </a>
              </li>
            ))}
          </ul>
        </nav>
      </header>

      {/* Hero Section */}
      <section id="home" className="hero">
        <div className="home-content">
          <h1>Welcome To <span c>Sarathi!</span></h1>
          <p><span className="multiple-text"></span></p>
          <h2>Book your Ride By Click Here!</h2>
          <button
          className="glow-btn"
          onClick={() =>
            document.getElementById("booking").scrollIntoView({ behavior: "smooth" })
          }
          >
            Book Now
          </button>

        </div>

