import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import "./PickupLocation.css";

// Fix Leaflet marker icon
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
  iconUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
  shadowUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
});

// Component to recenter the map
const RecenterMap = ({ position }) => {
  const map = useMap();
  useEffect(() => {
    if (position) {
      map.setView(position, 15); // Zoom in to user location
    }
  }, [position, map]);
  return null;
};

const PickupLocation = ({ setPickup }) => {
  const [position, setPosition] = useState(null);
  const [searchValue, setSearchValue] = useState("");
  const [predictions, setPredictions] = useState([]);
  const [mapReady, setMapReady] = useState(false);

  const savedAddresses = [
    { label: "Home", lat: 19.076, lng: 72.8777 },
    { label: "Office", lat: 19.2183, lng: 72.9781 },
  ];

  // Update pickup address automatically
  useEffect(() => {
    if (!position) return;
    fetch(
      `https://nominatim.openstreetmap.org/reverse?format=json&lat=${position.lat}&lon=${position.lng}`
    )
      .then((res) => res.json())
      .then((data) => {
        setPickup({
          lat: position.lat,
          lng: position.lng,
          address: data.display_name,
        });
        setSearchValue(data.display_name);
      });
  }, [position, setPickup]);

  // Autocomplete search
  const handleSearchChange = (e) => {
    setSearchValue(e.target.value);
    if (!e.target.value) return setPredictions([]);
    fetch(
      `https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(
        e.target.value
      )}&format=json&addressdetails=1&limit=5`
    )
      .then((res) => res.json())
      .then((data) => setPredictions(data));
  };

  const handleSelectPrediction = (place) => {
    setPosition({ lat: parseFloat(place.lat), lng: parseFloat(place.lon) });
    setPredictions([]);
  };

  const handleUseCurrentLocation = () => {
    if (!navigator.geolocation) {
      alert("Geolocation is not supported by your browser");
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setPosition({ lat: pos.coords.latitude, lng: pos.coords.longitude });
        setMapReady(true);
      },
      (err) => {
        console.error(err);
        alert("Unable to retrieve your location. Please allow location access.");
      },
      { enableHighAccuracy: true }
    );
  };

  return (
    <div className="pickup-container">
      <h2>Select Pickup Location</h2>

      <button className="current-location-btn" onClick={handleUseCurrentLocation}>
        Use Current Location
      </button>

      <input
        type="text"
        placeholder="Search for location"
        value={searchValue}
        onChange={handleSearchChange}
        className="search-input"
      />

      {predictions.length > 0 && (
        <ul className="predictions-list">
          {predictions.map((p) => (
            <li key={p.place_id} onClick={() => handleSelectPrediction(p)}>
              {p.display_name}
            </li>
          ))}
        </ul>
      )}

      <div className="saved-addresses">
        <h4>Saved Addresses</h4>
        {savedAddresses.map((a) => (
          <div
            key={a.label}
            className="saved-address"
            onClick={() => setPosition({ lat: a.lat, lng: a.lng })}
          >
            {a.label}
          </div>
        ))}
      </div>

      <div className="map-container">
        {mapReady || position ? (
          <MapContainer
            center={position || [20.5937, 78.9629]}
            zoom={position ? 15 : 5}
            style={{ height: "100%", width: "100%" }}
          >
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            {position && <Marker position={position} />}
            <RecenterMap position={position} />
          </MapContainer>
        ) : (
          <p style={{ textAlign: "center", paddingTop: "50px" }}>Map will appear here</p>
        )}
      </div>

      {position && (
        <p style={{ fontSize: "0.9rem", marginTop: "4px" }}>
          📍 Selected Address: {searchValue}
        </p>
      )}
    </div>
  );
};

export default PickupLocation;
