import React, { useState } from "react";
import BookingForm from "./components/BookingForm";
import "./App.css";

function App() {
  const [bookingSuccess, setBookingSuccess] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  const handleBooking = async (form) => {
    try {
      const response = await fetch("http://localhost:8080/api/bookings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      if (response.ok) {
        setBookingSuccess(true);
      } else {
        setBookingSuccess(false);
      }
    } catch (error) {
      console.error(error);
      setBookingSuccess(false);
    }
  };

  return (
    <div className="app-container">
      {/* ✅ Navbar */}
      <div className="top">
      <div className="logo">Sarathi</div>
      <nav className="navbar">

        <div
          className={`menu-toggle ${menuOpen ? "open" : ""}`}
          onClick={() => setMenuOpen(!menuOpen)}
        >
          ☰
        </div>
        <ul className={menuOpen ? "nav-links active" : "nav-links"}>
          <li><a href="#home" onClick={(e) => {
                e.preventDefault();
                document.getElementById("home").scrollIntoView({ behavior: "smooth" });
                setMenuOpen(false);
      }}>Home</a></li>
          <li><a href="#about" onClick={(e) => {
        e.preventDefault();
        document.getElementById("about").scrollIntoView({ behavior: "smooth" });
        setMenuOpen(false);
      }}>About Us</a></li>
          <li><a href="#services" onClick={(e) => {
        e.preventDefault();
        document.getElementById("services").scrollIntoView({ behavior: "smooth" });
        setMenuOpen(false);
      }}>Services</a></li>
          <li><a href="#contact" onClick={(e) => {
        e.preventDefault();
        document.getElementById("home").scrollIntoView({ behavior: "smooth" });
        setMenuOpen(false);
      }}>Contact</a></li>
        </ul>
      </nav>
      </div>

      {/* ✅ Hero Section */}
      <section id="home" className="hero">
        <h1>Welcome To Sarathi.</h1>
        <p>Book a professional driver for your vehicle anytime, anywhere.</p>
        <h2>Book your Ride By Click Here!</h2>
        <button
  className="glow-btn"
  onClick={() =>
    document.getElementById("booking").scrollIntoView({ behavior: "smooth" })
  }
>
  Book Now
</button>


      </section>

      {/* ✅ About Section */}
      <section id="about" className="section">
        <h2>About Us</h2>
        <p>
          We connect customers with professional drivers. Whether you need a driver
          for a few hours or a full day, we make it simple and safe.
        </p>
      </section>

      {/* ✅ Services Section */}
      <section id="services" className="section">
        <h2>Our Services</h2>
        <ul>
          <li>Hourly Driver Booking</li>
          <li>Full Day Driver Booking</li>
          <li>Outstation Travel Driver</li>
        </ul>
      </section>

      {/* ✅ Booking Form Section */}
      <section id="booking" className="booking-section">
        <BookingForm onSubmit={handleBooking} />
        {bookingSuccess && (
          <p className="success-msg">✅ Booking confirmed successfully!</p>
        )}
      </section>

      {/* ✅ Contact Section */}
      <section id="contact" className="section">
        <h2>Contact Us</h2>
        <p>Email: support@driverondemand.com</p>
        <p>Phone: +91-9876543210</p>
      </section>
    </div>
  );
}

export default App;
