import React, { useState, useEffect } from 'react';

export default function BookingForm({onSubmit, drivers}){
  const [form, setForm] = useState({
    customerName:'',
    phone:'',
    pickupLocation:'',
    pickupTime:'',
    durationHours:1,
    driverId: ''
  });

  useEffect(()=>{
    if(drivers && drivers.length && !form.driverId){
      setForm(prev => ({...prev, driverId: drivers[0].id}));
    }
  }, [drivers]);

  function update(e){
    const {name, value} = e.target;
    setForm(prev => ({...prev, [name]: value}));
  }

  function submit(e){
    e.preventDefault();
    onSubmit(form);
  }

  return (
    <form onSubmit={submit} style={{border:'1px solid #ddd', padding:16, borderRadius:8}}>
      <h3>Book a Driver</h3>
      <label>Customer Name<br/>
        <input name="customerName" value={form.customerName} onChange={update} required />
      </label>
      <br/>
      <label>Phone<br/>
        <input name="phone" value={form.phone} onChange={update} required />
      </label>
      <br/>
      <label>Pickup Location<br/>
        <input name="pickupLocation" value={form.pickupLocation} onChange={update} required />
      </label>
      <br/>
      <label>Pickup Time<br/>
        <input type="datetime-local" name="pickupTime" value={form.pickupTime} onChange={update} required />
      </label>
      <br/>
      <label>Duration (hours)<br/>
        <input type="number" name="durationHours" min={1} value={form.durationHours} onChange={update} />
      </label>
      <br/>
      <label>Choose Driver<br/>
        <select name="driverId" value={form.driverId || ''} onChange={update}>
          {drivers.map(d=> <option key={d.id} value={d.id}>{d.name} â€” {d.experience} yrs</option>)}
        </select>
      </label>
      <br/>
      <button type="submit" style={{marginTop:10}}>Confirm Booking</button>
    </form>
  );
}
