import React, { useState, useEffect, useRef } from "react";
import "./ServicesCarousel.css";
import driver from "../assets/driver.png";
import driver1 from "../assets/driver1.png";
import outstation from "../assets/outstation.png";
import event from "../assets/event.png";
import night from "../assets/night.png";
import airport from "../assets/airport.png";
import ScrollReveal from "scrollreveal";

const ServicesCarousel = () => {
  const services = [
    { id: 1, title: "Hourly Driver", img: driver },
    { id: 2, title: "Full Day Driver", img: driver1 },
    { id: 3, title: "Outstation Travel", img: outstation },
    { id: 4, title: "Event Driver", img: event },
    { id: 5, title: "Night Shift", img: night },
    { id: 6, title: "Airport Pickup", img: airport },
  ];

  const carouselRef = useRef(null);
  const animationRef = useRef(null);
  const startX = useRef(0);
  const rotationDegree = 360 / services.length;
  const rotation = useRef(0); // current rotation in degrees
  const isDragging = useRef(false);

  const [current, setCurrent] = useState(0);

  // ScrollReveal animation
  useEffect(() => {
    ScrollReveal().reveal(".carousel-title, .carousel3d-section", {
      duration: 1500,
      distance: "60px",
      easing: "ease-in-out",
      origin: "bottom",
      delay: 200,
      reset: false,
    });
  }, []);

  // Smooth continuous rotation
  const rotate = () => {
    if (!isDragging.current) {
      rotation.current += 0.1; // adjust speed here (smaller = slower)
      carouselRef.current.style.transform = `rotateY(${-rotation.current}deg)`;
      // Update active index
      const index = Math.round((rotation.current % 360) / rotationDegree) % services.length;
      setCurrent((services.length + index) % services.length);
    }
    animationRef.current = requestAnimationFrame(rotate);
  };

  useEffect(() => {
    animationRef.current = requestAnimationFrame(rotate);
    return () => cancelAnimationFrame(animationRef.current);
  }, []);

  // Drag / Swipe Support
  const handleMouseDown = (e) => {
    isDragging.current = true;
    startX.current = e.clientX || e.touches[0].clientX;
  };

  const handleMouseUp = (e) => {
    const endX = e.clientX || e.changedTouches[0].clientX;
    const diff = startX.current - endX;
    if (diff > 30) rotation.current += rotationDegree;
    if (diff < -30) rotation.current -= rotationDegree;
    isDragging.current = false;
  };

  // Click on dots
  const handleDotClick = (index) => {
    rotation.current = index * rotationDegree;
    setCurrent(index);
  };

  return (
    <div className="carousel3d-section">
      <h2 className="carousel-title">
        OUR <span>SERVICES</span>
      </h2>
      <div
        className="carousel3d-wrapper"
        onMouseDown={handleMouseDown}
        onMouseUp={handleMouseUp}
        onTouchStart={handleMouseDown}
        onTouchEnd={handleMouseUp}
      >
        <div className="carousel3d" ref={carouselRef}>
          {services.map((service, index) => (
            <div
              key={service.id}
              className="carousel3d-card"
              style={{
                "--y": `${index * rotationDegree}deg`,
                backgroundImage: `url(${service.img})`,
              }}
            >
              <div className="carousel3d-card-title">{service.title}</div>
            </div>
          ))}
        </div>
      </div>
      {/* Indicator Dots */}
      <div className="carousel-dots">
        {services.map((_, index) => (
          <span
            key={index}
            className={`dot ${index === current ? "active" : ""}`}
            onClick={() => handleDotClick(index)}
          ></span>
        ))}
      </div>
    </div>
  );
};

export default ServicesCarousel;
