import React, { useState, useRef } from "react";
import "./SwipableCards.css";

const SwipableCards = () => {
  const [cards, setCards] = useState([
    { id: 1, title: "Card One", description: "First card", color: "pink" },
    { id: 2, title: "Card Two", description: "Second card", color: "indigo" },
    { id: 3, title: "Card Three", description: "Third card", color: "yellow" },
    { id: 4, title: "Card Four", description: "Fourth card", color: "green" },
    { id: 5, title: "Card Five", description: "Fifth card", color: "gray" }
  ]);

  const containerRef = useRef(null);

  const handleSwipe = (direction) => {
    if (cards.length === 0) return;
    const [first, ...rest] = cards;
    setCards(rest);
  };

  return (
    <div className="cards-page">
      <div className="cards-container" ref={containerRef}>
        {cards.length === 0 && <div className="empty-message">No more cards</div>}
        {cards.map((card, index) => (
          <div key={card.id} className={`card ${card.color}`}>
            <div className="card-body">
              <div className="card-content">
                <h3>{card.title}</h3>
                <p>{card.description}</p>
              </div>
            </div>
            <div className="card-footer">
              <div className="actions">
                <button onClick={() => handleSwipe("left")}>Dislike</button>
                <button onClick={() => handleSwipe("right")}>Like</button>
              </div>
              <span className="card-id">#{card.id}</span>
            </div>
          </div>
        ))}
        <div className="controls">
          <button onClick={() => handleSwipe("left")}>⟵</button>
          <button onClick={() => handleSwipe("right")}>⟶</button>
        </div>
      </div>
    </div>
  );
};

export default SwipableCards;
