import React, { useState, useEffect, useRef } from "react";
import "./ServicesCarousel.css";
import driver from "../assets/driver.png";
import driver1 from "../assets/driver1.png";
import outstation from "../assets/outstation.png";
import event from "../assets/event.png";
import night from "../assets/night.png";
import airport from "../assets/airport.png";
import ScrollReveal from "scrollreveal";

const ServicesCarousel = () => {
  const services = [
    { id: 1, title: "Hourly Driver", img: driver },
    { id: 2, title: "Full Day Driver", img: driver1 },
    { id: 3, title: "Outstation Travel", img: outstation },
    { id: 4, title: "Event Driver", img: event },
    { id: 5, title: "Night Shift", img: night },
    { id: 6, title: "Airport Pickup", img: airport },
  ];

  const carouselRef = useRef(null);
  const intervalRef = useRef(null);
  const isDragging = useRef(false);
  const startX = useRef(0);
  const rotationY = useRef(0);

  const rotationDegree = 360 / services.length;

  // Auto-rotate carousel
  const startAutoRotate = () => {
    intervalRef.current = setInterval(() => {
      rotationY.current -= rotationDegree; // rotate left automatically
      if (carouselRef.current) {
        carouselRef.current.style.transform = `rotateY(${rotationY.current}deg)`;
      }
    }, 3000);
  };

  useEffect(() => {
    startAutoRotate();

    // ScrollReveal animations
    ScrollReveal().reveal(".carousel3d-section", {
      duration: 2000,
      distance: "80px",
      easing: "ease-in-out",
      origin: "bottom",
      delay: 200,
      reset: false,
    });
    ScrollReveal().reveal(".carousel-title", {
      duration: 2000,
      distance: "60px",
      easing: "ease-in-out",
      origin: "top",
      delay: 200,
      reset: false,
    });

    return () => clearInterval(intervalRef.current);
  }, []);

  // Mouse / touch events
  const onDragStart = (clientX) => {
    isDragging.current = true;
    startX.current = clientX;
    clearInterval(intervalRef.current); // pause auto-rotate
  };

  const onDragMove = (clientX) => {
    if (!isDragging.current) return;
    const deltaX = clientX - startX.current;
    startX.current = clientX;
    rotationY.current += deltaX * 0.5; // sensitivity
    if (carouselRef.current) {
      carouselRef.current.style.transform = `rotateY(${rotationY.current}deg)`;
    }
  };

  const onDragEnd = () => {
    isDragging.current = false;
    startAutoRotate(); // resume auto-rotate
  };

  return (
    <div className="carousel3d-section">
      <h2 className="carousel-title">
        OUR <span>SERVICES</span>
      </h2>
      <div
        className="carousel3d-wrapper"
        onMouseDown={(e) => onDragStart(e.clientX)}
        onMouseMove={(e) => onDragMove(e.clientX)}
        onMouseUp={onDragEnd}
        onMouseLeave={onDragEnd}
        onTouchStart={(e) => onDragStart(e.touches[0].clientX)}
        onTouchMove={(e) => onDragMove(e.touches[0].clientX)}
        onTouchEnd={onDragEnd}
      >
        <div className="carousel3d" ref={carouselRef}>
          {services.map((service, index) => (
            <div
              key={service.id}
              className="carousel3d-card"
              style={{
                "--y": `${index * rotationDegree}deg`,
                backgroundImage: `url(${service.img})`,
              }}
            >
              <div className="carousel3d-card-title">{service.title}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ServicesCarousel;
