import React, { useState } from "react";
import BookingForm from "./components/BookingForm";
import ServicesCarousel from "./components/ServicesCarosel";
import "./App.css";

import FacebookIcon from '@mui/icons-material/Facebook';
import InstagramIcon from '@mui/icons-material/Instagram';
import TwitterIcon from '@mui/icons-material/Twitter';
import WhatsAppIcon from '@mui/icons-material/WhatsApp';

import image2 from "./assets/image2.png";

function App() {
  const [bookingSuccess, setBookingSuccess] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  const handleBooking = async (form) => {
    try {
      const response = await fetch("http://localhost:8080/api/bookings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      if (response.ok) {
        setBookingSuccess(true);
      } else {
        setBookingSuccess(false);
      }
    } catch (error) {
      console.error(error);
      setBookingSuccess(false);
    }
  };

  const scrollTo = (id) => {
    document.getElementById(id).scrollIntoView({ behavior: "smooth" });
    setMenuOpen(false);
  };

  return (
    <div className="app-container">

      {/* Navbar */}
      <div className="top">
        <div className="logo">Sarathi</div>
        <nav className="navbar">
          <div
            className={`menu-toggle ${menuOpen ? "open" : ""}`}
            onClick={() => setMenuOpen(!menuOpen)}
          >
            ☰
          </div>
          <ul className={`nav-links ${menuOpen ? "active" : ""}`}>
            <li><a href="#home" onClick={() => scrollTo("home")}>Home</a></li>
            <li><a href="#about" onClick={() => scrollTo("about")}>About Us</a></li>
            <li><a href="#services" onClick={() => scrollTo("services")}>Services</a></li>
            <li><a href="#contact" onClick={() => scrollTo("contact")}>Contact</a></li>
          </ul>
        </nav>
      </div>

      {/* Hero Section */}
      <div id="home" className="hero">
        <h1>Welcome To Sarathi.</h1>
        <p>Book a professional driver for your vehicle anytime, anywhere.</p>
        <h2>Book your Ride By Click Here!</h2>
        <button className="glow-btn" onClick={() => scrollTo("booking")}>
          Book Now
        </button>
      </div>

      {/* About Section */}
      <div id="about" className="section about-section">
        <h2>ABOUT US</h2>
        <div className="about-content">
          <ul>
            <li>We connect customers with professional drivers.</li>
            <li>Whether you need a driver for a few hours or a full day, we make it simple and safe.</li>
          </ul>
          <div className="about-image">
            <img src={image2} alt="About Sarathi" />
          </div>
        </div>
      </div>

      {/* Services Section */}
      <div id="services" className="section">
        <ServicesCarousel />
      </div>

      {/* Booking Form */}
      <div id="booking" className="section booking-section">
        <BookingForm onSubmit={handleBooking} />
        {bookingSuccess && <p className="success-msg">✅ Booking confirmed successfully!</p>}
      </div>

      {/* Contact Section */}
      <div id="contact" className="section contact-section">
        <h2>Contact Us</h2>
        <div className="contact-cards">
          <div className="contact-card email-card">
            <a href="mailto:support@driverondemand.com">
              <div className="contact-content">
                <p>support@driverondemand.com</p>
              </div>
            </a>
          </div>
          <div className="contact-card phone-card">
            <a href="tel:+919876543210">
              <div className="contact-content">
                <p>+91-9876543210</p>
              </div>
            </a>
          </div>
        </div>
      </div>

      {/* Footer */}
      <div className="footer-section">
        <div className="footer-content">
          <div>
            <div className="heading">BUSINESS TIME</div>
            <p className="list">24/7 service</p>
          </div>
          <div>
            <div className="heading">QUICK ACCESS</div>
            <ul className="foot-links">
              <li><a href="#home" onClick={() => scrollTo("home")}>Home</a></li>
              <li><a href="#services" onClick={() => scrollTo("services")}>Services</a></li>
              <li><a href="#about" onClick={() => scrollTo("about")}>About</a></li>
              <li><a href="#booking" onClick={() => scrollTo("booking")}>Booking</a></li>
              <li><a href="#contact" onClick={() => scrollTo("contact")}>Contact</a></li>
            </ul>
          </div>
          <div>
            <div className="heading">CONTACT</div>
            <p>Email: support@driverondemand.com</p>
            <p>Phone: +91-9876543210</p>
          </div>
          <div>
            <div className="heading">FOLLOW US</div>
            <div className="icons">
              <a href="https://facebook.com" target="_blank" rel="noopener noreferrer"><FacebookIcon /></a>
              <a href="https://wa.me/919876543210" target="_blank" rel="noopener noreferrer"><WhatsAppIcon /></a>
              <a href="https://instagram.com" target="_blank" rel="noopener noreferrer"><InstagramIcon /></a>
              <a href="https://twitter.com" target="_blank" rel="noopener noreferrer"><TwitterIcon /></a>
            </div>
          </div>
        </div>
        <div className="footer-bottom">
          <p>© 2025 DriverOnDemand. All Rights Reserved.</p>
        </div>
      </div>

    </div>
  );
}

export default App;
